import {ShopActionTypes} from './shop.types';
import { firestore,convertCollectionSnapshotToMap } from '../../firebase/firebase.utlis';


export const  fetchCollectionsStart = (collectionMap) => ({

     type: ShopActionTypes.UPDATE_COLLECTIONS,
     payload: collectionMap

});

export const fetchCollectionsSuccess = (collectionMap) => ({

     type:ShopActionTypes.FETCH_COLLECTIONS_SUCCESS,
     payload: collectionMap


});


export const fetchCollectionsFailure = (e) => ({

   type: ShopActionTypes.FETCH_COLLECTIONS_FAILURE,
   payload: collectionMap

});



export const fetchCollectionsStartAsync =()=>{

     return dispatch => {

          const collectionRef = firestore.collection('collections');
          dispatch(fetchCollectionsStart());
        collectionRef.get()
        .then(snapshot =>{

                const collectionMap = convertCollectionSnapshotToMap(snapshot);
                 dispatch(fetchCollectionsSuccess(collectionMap));

              })
              .catch(error =>{

                dispatch(fetchCollectionsFailure(error));


              })

              ;

     }

}
